mapper.select:
select id, name, mailid, start_date, manager_id, title, dept_id    ,                      
ifnull(salary,0.0) salary, ifnull(commission_pct,0.0) commission_pct                                    
from s_emp where name like  concat('%',#{name},'%')

mapper.insert:
INSERT INTO s_emp (id, name, mailid, start_date, manager_id, title, dept_id, SALARY, COMMISSION_PCT) VALUES(#{id}, #{name},#{mailid},#{start_date},
#{manager_id}
null
,#{title},#{dept_id},#{salary},
#{commission_pct}
null
)

mapper.update:
UPDATE s_emp SET name=#{name}, mailid=#{mailid} , start_date= #{start_date}, manager_id=#{manager_id}, title=#{title}, dept_id=#{dept_id},salary=#{salary}
,commission_pct=#{commission_pct}
,commission_pct=null
WHERE id=#{id}

mapper.select:
select id, name, mailid, start_date, manager_id, title, dept_id    ,                      
ifnull(salary,0.0) salary, ifnull(commission_pct,0.0) commission_pct                                        
from s_emp WHERE DEPT_ID=#{dept_id}
